sp_angular_provider.script:
function($http){
	var config = {
		params: {
			sysparm_fields: 'number, short_description, priority, assigned_to, state, sys_id',
			sysparm_display_value: 'all'
		}
	}

	var apiBaseUrl = function(tableName){
		return '/api/now/table/' + tableName;
	}

	this.tasks = [];
	var self = this;

	this.getTasks = function(tableName, encodedQuery, startPos, rowCount){
		config.params.sysparm_query = encodedQuery + '^ORDERBYDESCnumber';
		config.params.sysparm_limit = rowCount || '';
		config.params.sysparm_offset = startPos || 0;
		return $http.get(apiBaseUrl(tableName), config);
	};

	this.appendTasks = function(tableName, encodedQuery, startPos, rowCount){ 
		return this.getTasks(tableName, encodedQuery, startPos, rowCount).then(function(response){
		self.tasks = self.tasks.concat(response.data.result);
	});
	};


}
