sp_widget.client_script:
function(spUtil, $scope) {
	/* widget controller */
	var c = this;

	//add record watcher for selected table and filter
	//refresh the data when any update/insert/delete occurs
	if (c.data.isValid){
		spUtil.recordWatch($scope, c.data.tableName, c.data.filter, function(name,data){
			c.server.refresh();
		});
	}

	//returns an appropriate CSS class for priority label
	c.getPriorityClass = function (task){
		var priority = task.priority.value;
		return {
			'label': true,
			'label-danger': priority == '1',
			'label-warning': priority == '2',
			'label-primary': priority == '3',
			'label-success': priority == '4',
			'label-default': priority == '5'
		}

	}

}

sp_widget.script:
(function () {

	//read url params
	data.tableName = $sp.getParameter('table') || options.table_name;
	data.filter = $sp.getParameter('filter') || options.filter || '';
	var limit = options.limit || 10;
	//check the empty page in designer
	if (gs.nil($sp.getParameter('id'))){
		data.tableName = 'incident';
	}

	//set the field names to read
	var fieldNames = 'number, short_description, priority, assigned_to, state, sys_id';
	//fetch data from server
	data.isValid = false;
	data.tasks = getTaskRecords(data.tableName, data.filter);


	/**
	 * returns an array of record objects from the specified
	 * gliderecord table name and filter
	 * @param   {string} tableName sys_name of the table
	 * @param   {string} filter    encoded query
	 * @returns {Array}  array of object record representation, returns empty array if not valid
	 *                   or zero records found
	 */
	function getTaskRecords(tableName, filter) {
		var records = [];
		if (!gs.nil(tableName)) {
			var recordGR = new GlideRecordSecure(data.tableName);
			if (recordGR.isValid()) {
				data.isValid = true;
				data.label = recordGR.getClassDisplayValue();
				recordGR.addEncodedQuery(filter);
				recordGR.orderByDesc('sys_updated_on');
				recordGR.setLimit(limit);
				recordGR.query();
				while (recordGR.next()) {
					records.push($sp.getFieldsObject(recordGR, fieldNames));
				}
			}
		} else {
			return [];
		}
		return records;
	}
})();
